import React, { useState, useEffect } from 'react';
import api from '../services/api';

function ProductList() {
  const [Products, setProducts] = useState([]);
  const [novoProduct, setNovoProduct] = useState('');
  const [loading, setLoading] = useState(false);

  // Carregar Products ao iniciar
  useEffect(() => {
    carregarProducts();
  }, []);

  // Função para buscar Products do backend
  const carregarProducts = async () => {
    try {
      setLoading(true);
      const response = await api.get('/Product'); // Chama GET /api/Product
      setProducts(response.data);
    } catch (error) {
      console.error('Erro ao carregar Products:', error);
      alert('Erro ao carregar Products');
    } finally {
      setLoading(false);
    }
  };

  // Função para adicionar Product
  const adicionarProduct = async () => {
    if (!novoProduct.trim()) return;

    try {
      const Product = { nome: novoProduct };
      const response = await api.post('/Product', Product); // Chama POST /api/Product

      // Adiciona o novo Product à lista
      setProducts([...Products, response.data]);
      setNovoProduct('');
      alert('Product adicionado com sucesso!');
    } catch (error) {
      console.error('Erro ao adicionar Product:', error);
      alert('Erro ao adicionar Product');
    }
  };

  // Função para deletar Product
  const deletarProduct = async (id) => {
    try {
      await api.delete(`/Product/${id}`); // Chama DELETE /api/Product/{id}
      setProducts(Products.filter(p => p.id !== id));
      alert('Product deletado com sucesso!');
    } catch (error) {
      console.error('Erro ao deletar Product:', error);
    }
  };

  return (
    <div>
      <h2>Lista de Products</h2>

      {/* Formulário para adicionar */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={novoProduct}
          onChange={(e) => setNovoProduct(e.target.value)}
          placeholder="Nome do Product"
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <button onClick={adicionarProduct} style={{ padding: '8px 16px' }}>
          Adicionar
        </button>
        <button onClick={carregarProducts} style={{ marginLeft: '10px', padding: '8px 16px' }}>
          Atualizar
        </button>
      </div>

      {/* Lista de Products */}
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <ul>
          {Products.map((Product, index) => (
            <li key={Product.id || index} style={{ marginBottom: '10px' }}>
              {Product.nome || Product}
              <button 
                onClick={() => deletarProduct(Product.id || index)}
                style={{ marginLeft: '10px', padding: '5px 10px' }}
              >
                Excluir
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ProductList;
