import React, { useState, useEffect } from 'react';
import api from '../services/api';

function ProductsList() {
  const [Productss, setProductss] = useState([]);
  const [novoProducts, setNovoProducts] = useState({name:'', price:0, categoryName:'', CategoryId:0});
  const [loading, setLoading] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    carregarProductss();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNovoProducts(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const carregarProductss = async () => {
    try {
      setLoading(true);
      const response = await api.get('/Products');
      setProductss(response.data);
    } catch (error) {
      console.error('Erro ao carregar Productss:', error);
    } finally {
      setLoading(false);
    }
  };

  // --- ADICIONAR ---
  const adicionarProducts = async () => {
    try {
      const payload = { 
        name: novoProducts.name, 
        price: Number(novoProducts.price), 
        CategoryId: Number(novoProducts.CategoryId) 
      };

      await api.post('/Products', payload);

      // CORREÇÃO: Em vez de adicionar manualmente, recarregamos a lista.
      // Isso força o backend a mandar a lista nova com o Nome da Categoria correto.
      await carregarProductss(); 

      setNovoProducts({name:'', price:0, categoryName:'', CategoryId:0});
    } catch (error) {
      console.error('Erro ao adicionar Products:', error);
      alert('Erro ao adicionar');
    }
  };

  // --- PREPARAR EDIÇÃO ---
  const prepararEdicao = (produto) => {
    setEditId(produto.id);
    setNovoProducts({
      name: produto.name,
      price: produto.price,
      CategoryId: produto.CategoryId,
      categoryName: produto.categoryName // Mantém visualmente até salvar
    });
  };

  // --- ATUALIZAR (UPDATE) ---
  const updateProdutos = async () => {
    if (!editId) return;

    try {
      const payload = { 
        name: novoProducts.name, 
        price: Number(novoProducts.price),
        CategoryId: Number(novoProducts.CategoryId) 
      };

      await api.put(`/Products/${editId}`, payload);

      // CORREÇÃO: Recarrega a lista para pegar o nome da categoria atualizado do banco
      await carregarProductss();

      // Limpa os estados
      setEditId(null);
      setNovoProducts({name:'', price:0, categoryName:'', CategoryId:0});

    } catch (error) {
      console.error('Erro ao atualizar Products:', error);
      alert('Erro ao atualizar. Verifique se o ID existe.');
    }
  };

  const deletarProducts = async (id) => {
    if(!window.confirm("Deseja excluir?")) return;
    try {
      await api.delete(`/Products/${id}`);
      // Aqui podemos filtrar localmente pois deletar não precisa de dados novos
      setProductss(Productss.filter(p => p.id !== id));
    } catch (error) {
      console.error('Erro ao deletar Products:', error);
    }
  };

  return (
      <div>
      <h2>Lista de Produtos</h2>

      <div style={{ marginBottom: '20px' }}>
        <label>Produto: </label>
        <input
          type="text"
          value={novoProducts.name}
          onChange={handleInputChange}
          name="name"
          placeholder="Nome"
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <label>Preço: </label>
        <input
          type="number"
          value={novoProducts.price}
          onChange={handleInputChange}
          name="price"
          placeholder="Preço"
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <label>Id Categoria: </label>
        <input
          type="number"
          value={novoProducts.CategoryId}
          onChange={handleInputChange}
          name="CategoryId"
          placeholder="ID Categoria"
          style={{ padding: '8px', marginRight: '10px' }}
        />

        {/* Lógica dos Botões */}
        {!editId ? (
          <button onClick={adicionarProducts} style={{ padding: '8px 16px' }}>
            Adicionar
          </button>
        ) : (
          <>
            <button onClick={updateProdutos} style={{ padding: '8px 16px', backgroundColor: 'orange' }}>
              Salvar Alteração
            </button>
            <button 
              onClick={() => { setEditId(null); setNovoProducts({name:'', price:0, categoryName:'', CategoryId:0}); }} 
              style={{ marginLeft: '10px', padding: '8px 16px' }}
            >
              Cancelar
            </button>
          </>
        )}
      </div>

      {loading ? (
        <p>Carregando...</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>Produto</th>
              <th style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>Valor</th>
              <th style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>Categoria</th>
              <th style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>Ação</th>
            </tr>
          </thead>
          <tbody>
          {Productss.map((product, index) => (
            <tr key={product.id || index} style={{ marginBottom: '10px' }}>

              <td style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>{product.name}</td>
              <td style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>{product.price}</td>
              <td style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>{product.categoryName}</td>

              <td style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>
                <button 
                  onClick={() => prepararEdicao(product)}
                  style={{ marginRight: '10px', padding: '5px 10px' }}
                >
                  Editar
                </button>
                <button 
                  onClick={() => deletarProducts(product.id || index)}
                  style={{ marginLeft: '10px', padding: '5px 10px' }}
                >
                  Excluir
                </button>
              </td>
            </tr>
          ))}
          </tbody>
        </table>
      )}
      </div>
  );
}

export default ProductsList;
