import React, { useState, useEffect } from 'react';
import api from '../services/api';

function CategoryList() {
  const [Categoriess, setCategoriess] = useState([]);

  // 1. Estado inicial correto (objeto, não string)
  const initialFormState = { name: '' };
  const [novoCategories, setNovoCategories] = useState(initialFormState);

  const [loading, setLoading] = useState(false);

  // 2. Estado para controlar qual ID está sendo editado
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    carregarCategoriess();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNovoCategories(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const carregarCategoriess = async () => {
    try {
      setLoading(true);
      const response = await api.get('/Category');
      setCategoriess(response.data);
    } catch (error) {
      console.error('Erro ao carregar Categorias:', error);
      alert('Erro ao carregar Categorias');
    } finally {
      setLoading(false);
    }
  };

  // --- ADICIONAR (CREATE) ---
  const adicionarCategories = async () => {
    try {
      // Ajuste: Categorias só precisam de name, removi price e CategoryId
      const categoryPayload = { name: novoCategories.name };

      const response = await api.post('/Category', categoryPayload);

      setCategoriess([...Categoriess, response.data]);
      setNovoCategories(initialFormState); // Limpa corretamente

    } catch (error) {
      console.error('Erro ao adicionar Categoria:', error);
      alert('Erro ao adicionar Categoria');
    }
  };

  // --- PREPARAR EDIÇÃO (Jogar dados para o input) ---
  const iniciarEdicao = (categoria) => {
    setEditId(categoria.id);
    setNovoCategories({ name: categoria.name });
  };

  // --- CANCELAR EDIÇÃO ---
  const cancelarEdicao = () => {
    setEditId(null);
    setNovoCategories(initialFormState);
  };

  // --- ATUALIZAR (UPDATE - PUT) ---
  const updateCategories = async () => {
    if (!editId) return;

    try {
      const categoryPayload = { name: novoCategories.name };

      // Chama PUT /Category/{id}
      await api.put(`/Category/${editId}`, categoryPayload);

      // Atualiza a lista localmente sem precisar recarregar tudo
      const listaAtualizada = Categoriess.map(c => 
        c.id === editId ? { ...c, name: novoCategories.name } : c
      );

      setCategoriess(listaAtualizada);
      cancelarEdicao(); // Sai do modo de edição

    } catch (error) {
      console.error('Erro ao atualizar Categoria:', error);
      alert('Erro ao atualizar Categoria');
    }
  };

  // --- DELETAR (DELETE) ---
  const deletarCategories = async (id) => {
    if(!window.confirm("Deseja realmente excluir esta categoria?")) return;

    try {
      await api.delete(`/Category/${id}`);
      setCategoriess(Categoriess.filter(p => p.id !== id));
    } catch (error) {
      console.error('Erro ao deletar Categoria:', error);
      alert('Erro ao deletar');
    }
  };

  return (
    <div>
      <h2>Lista de Categorias</h2>

      {/* Formulário Inteligente (Serve para Adicionar e Editar) */}
      <div style={{ marginBottom: '20px', padding: '15px', border: '1px solid #ccc', borderRadius: '5px' }}>
        <h3 style={{marginTop: 0}}>{editId ? 'Editar Categoria' : 'Nova Categoria'}</h3>

        <label>Nome da Categoria: </label>
        <input
          type="text"
          value={novoCategories.name}
          onChange={handleInputChange}
          name="name"
          placeholder="Ex: Bebidas, Eletrônicos..."
          style={{ padding: '8px', marginRight: '10px', width: '300px' }}
        />

        {/* Botões mudam dependendo se é Edição ou Criação */}
        {editId ? (
          <>
            <button onClick={updateCategories} style={{ padding: '8px 16px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
              Salvar
            </button>
            <button onClick={cancelarEdicao} style={{ marginLeft: '10px', padding: '8px 16px', backgroundColor: '#f44336', color: 'white', border: 'none', cursor: 'pointer' }}>
              Cancelar
            </button>
          </>
        ) : (
          <button onClick={adicionarCategories} style={{ padding: '8px 16px', backgroundColor: '#008CBA', color: 'white', border: 'none', cursor: 'pointer' }}>
            Adicionar
          </button>
        )}
      </div>

      {/* Tabela de Categorias */}
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>Id</th>
              <th style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>Categoria</th>
              <th style={{border: '1px solid #dddddd', textAlign: 'center', padding: '8px'}}>Ações</th>
            </tr>
          </thead>
          <tbody>
            {Categoriess.map((c, index) => (
              <tr key={c.id || index} style={{ marginBottom: '10px' }}>
                <td style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>{c.id}</td>
                <td style={{border: '1px solid #dddddd', textAlign: 'left', padding: '8px'}}>{c.name}</td>

                <td style={{border: '1px solid #dddddd', textAlign: 'center', padding: '8px'}}>
                  {/* Botão Editar */}
                  <button 
                    onClick={() => iniciarEdicao(c)}
                    style={{ marginRight: '10px', padding: '5px 10px', cursor: 'pointer' }}
                  >
                    Editar
                  </button>

                  {/* Botão Excluir */}
                  <button 
                    onClick={() => deletarCategories(c.id)}
                    style={{ padding: '5px 10px', cursor: 'pointer', backgroundColor: '#ffcccc' }}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default CategoryList;
