import React, { useState, useEffect } from 'react';
import api from '../services/api';

function ProductsList() {
  const [productss, setProductss] = useState([]);

  // Estado inicial do formulário para facilitar o reset
  const initialFormState = { name: '', price: 0, categoryName: '', CategoryId: 0 };
  const [novoProducts, setNovoProducts] = useState(initialFormState);

  const [loading, setLoading] = useState(false);

  // Estado para saber qual ID estamos editando (null = modo criação)
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    carregarProductss();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNovoProducts(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const carregarProductss = async () => {
    try {
      setLoading(true);
      const response = await api.get('/Products');
      setProductss(response.data);
    } catch (error) {
      console.error('Erro ao carregar Productss:', error);
      alert('Erro ao carregar lista');
    } finally {
      setLoading(false);
    }
  };

  // --- LÓGICA DE ADICIONAR ---
  const adicionarProducts = async () => {
    try {
      const payload = { 
        name: novoProducts.name, 
        price: Number(novoProducts.price), // Garantir que é numero
        CategoryId: Number(novoProducts.CategoryId) 
      };

      const response = await api.post('/Products', payload);

      setProductss([...productss, response.data]);
      setNovoProducts(initialFormState); // Limpa o form corretamente

    } catch (error) {
      console.error('Erro ao adicionar:', error);
      alert('Erro ao adicionar');
    }
  };

  // --- LÓGICA DE PREPARAR EDIÇÃO (Ao clicar no botão da tabela) ---
  const iniciarEdicao = (produto) => {
    setEditId(produto.id); // Marca o ID que está sendo editado
    setNovoProducts({ 
      name: produto.name, 
      price: produto.price, 
      CategoryId: produto.CategoryId, // Certifique-se que seu backend retorna o ID da categoria
      categoryName: produto.categoryName 
    });
  };

  // --- LÓGICA DE ATUALIZAR (Enviar PUT) ---
  const updateProdutos = async () => {
    if (!editId) return; // Segurança

    try {
      const payload = { 
        name: novoProducts.name, 
        price: Number(novoProducts.price),
        CategoryId: Number(novoProducts.CategoryId) 
      };

      // Geralmente usa-se .put para atualizar
      const response = await api.put(`/Products/${editId}`, payload);

      // Atualiza a lista local trocando apenas o item modificado
      const listaAtualizada = productss.map(p => 
        p.id === editId ? response.data : p
      );

      setProductss(listaAtualizada);

      // Reseta tudo para o modo de "Adicionar"
      cancelarEdicao();

    } catch (error) {
      console.error('Erro ao atualizar:', error);
      alert('Erro ao atualizar produto');
    }
  };

  // Função para cancelar a edição e limpar form
  const cancelarEdicao = () => {
    setEditId(null);
    setNovoProducts(initialFormState);
  };

  const deletarProducts = async (id) => {

    try {
      await api.delete(`/Products/${id}`);
      setProductss(productss.filter(p => p.id !== id));
    } catch (error) {
      console.error('Erro ao deletar:', error);
    }
  };

  return (
    <div>
      <h2>Lista de Produtos</h2>

      {/* Formulário */}
      <div style={{ marginBottom: '20px', padding: '15px', border: '1px solid #ccc', borderRadius: '5px' }}>
        <h3>{editId ? 'Editar Produto' : 'Novo Produto'}</h3>

        <label>Produto: </label>
        <input
          type="text"
          value={novoProducts.name}
          onChange={handleInputChange}
          name="name"
          placeholder="Nome"
          style={{ padding: '8px', marginRight: '10px' }}
        />

        <label>Preço: </label>
        <input
          type="number"
          value={novoProducts.price}
          onChange={handleInputChange}
          name="price"
          placeholder="Preço"
          style={{ padding: '8px', marginRight: '10px' }}
        />

        <label>Id Categoria: </label>
        <input
          type="number"
          value={novoProducts.CategoryId}
          onChange={handleInputChange}
          name="CategoryId"
          placeholder="ID Categoria"
          style={{ padding: '8px', marginRight: '10px' }}
        />

        {/* Botões Condicionais */}
        {editId ? (
          <>
            <button onClick={updateProdutos} style={{ padding: '8px 16px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}>
              Salvar Alterações
            </button>
            <button onClick={cancelarEdicao} style={{ marginLeft: '10px', padding: '8px 16px', backgroundColor: '#f44336', color: 'white', border: 'none' }}>
              Cancelar
            </button>
          </>
        ) : (
          <button onClick={adicionarProducts} style={{ padding: '8px 16px', backgroundColor: '#008CBA', color: 'white', border: 'none' }}>
            Adicionar
          </button>
        )}
      </div>

      {/* Lista */}
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #dddddd', padding: '8px', textAlign: 'left' }}>Produto</th>
              <th style={{ border: '1px solid #dddddd', padding: '8px', textAlign: 'left' }}>Valor</th>
              <th style={{ border: '1px solid #dddddd', padding: '8px', textAlign: 'left' }}>Categoria</th>
              <th style={{ border: '1px solid #dddddd', padding: '8px', textAlign: 'center' }}>Ações</th>
            </tr>
          </thead>
          <tbody>
            {productss.map((product) => (
              <tr key={product.id}>
                <td style={{ border: '1px solid #dddddd', padding: '8px' }}>{product.name}</td>
                <td style={{ border: '1px solid #dddddd', padding: '8px' }}>{product.price}</td>
                <td style={{ border: '1px solid #dddddd', padding: '8px' }}>{product.categoryName}</td>
                <td style={{ border: '1px solid #dddddd', padding: '8px', textAlign: 'center' }}>

                  {/* Botão de Editar na linha */}
                  <button 
                    onClick={() => iniciarEdicao(product)}
                    style={{ marginRight: '5px', padding: '5px 10px', cursor: 'pointer' }}
                  >
                    Editar
                  </button>

                  <button 
                    onClick={() => deletarProducts(product.id)}
                    style={{ padding: '5px 10px', cursor: 'pointer', backgroundColor: '#ffcccc' }}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ProductsList;
