import React, { useState, useEffect } from 'react';
import api from '../services/api';

function CategoryList() {
  const [Categoriess, setCategoriess] = useState([]);
  const [novoCategories, setNovoCategories] = useState({name:''});
  const [loading, setLoading] = useState(false);

  // Carregar Categoriess ao iniciar
  useEffect(() => {
    carregarCategoriess();
  }, []);

  const handleInputChange = (e) => {
    // 1. Pega o nome do campo (ex: 'price') e o valor digitado (ex: '10')
    const { name, value } = e.target;

    // 2. Atualiza o estado
    setNovoCategories(prevState => ({
      ...prevState,    // Copia tudo o que já estava lá (para não apagar os outros campos)
      [name]: value    // Atualiza SÓ o campo que mudou
    }));
  };
  // Função para buscar Categoriess do backend
  const carregarCategoriess = async () => {
    try {
      setLoading(true);
      const response = await api.get('/Category'); // Chama GET /api/Categories
      setCategoriess(response.data);
    } catch (error) {
      console.error('Erro ao carregar Categoriess:', error);
      alert('Erro ao carregar Categoriess');
    } finally {
      setLoading(false);
    }
  };

  // Função para adicionar Categories
  const adicionarCategories = async () => {


    try {
      const Categories = { name: novoCategories.name, price: novoCategories.price, CategoryId: novoCategories.CategoryId };
      const response = await api.post('/Category', Categories); // Chama POST /api/Categories

      // Adiciona o novo Categories à lista
      setCategoriess([...Categoriess, response.data]);
      setNovoCategories('');

      alert('Categories adicionado com sucesso!');
    } catch (error) {
      console.error('Erro ao adicionar Categories:', error);
      alert('Erro ao adicionar Categories');
    }
  };

  // Função para deletar Categories
  const deletarCategories = async (id) => {
    try {
      await api.delete(`/Category/${id}`); // Chama DELETE /api/Categories/{id}
      setCategoriess(Categoriess.filter(p => p.id !== id));
      alert('Categories deletado com sucesso!');
    } catch (error) {
      console.error('Erro ao deletar Categories:', error);
    }
  };

  return (

      <div>
      <h2>Lista de Categoriess</h2>

      {/* Formulário para adicionar */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={novoCategories.name}
          onChange={handleInputChange}
          name="name"
          placeholder="nome do produto"
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <input
          type="number"
          value={novoCategories.price}
          onChange={handleInputChange}
          name="price"
          placeholder="preço do produto"
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <input
          type="number"
          value={novoCategories.id}
          onChange={handleInputChange}
          name="CategoryId"
          placeholder="id da categoria"
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <button onClick={adicionarCategories} style={{ padding: '8px 16px' }}>
          Adicionar
        </button>
        <button onClick={carregarCategoriess} style={{ marginLeft: '10px', padding: '8px 16px' }}>
          Atualizar
        </button>
      </div>

      {/* Lista de Categoriess */}
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <ul>
          {Categoriess.map((Categories, index) => (
            <li key={Categories.id || index} style={{ marginBottom: '10px' }}>
              {Categories.name || Categories}
              <button 
                onClick={() => deletarCategories(Categories.id || index)}
                style={{ marginLeft: '10px', padding: '5px 10px' }}
              >
                Excluir
              </button>
            </li>
          ))}
        </ul>
      )}
      </div>
  );
}

export default CategoryList;
