import React, { useState, useEffect } from 'react';
import api from '../services/api';

function ProdutoList() {
  const [produtos, setProdutos] = useState([]);
  const [novoProduto, setNovoProduto] = useState('');
  const [loading, setLoading] = useState(false);

  // Carregar produtos ao iniciar
  useEffect(() => {
    carregarProdutos();
  }, []);

  // Função para buscar produtos do backend
  const carregarProdutos = async () => {
    try {
      setLoading(true);
      const response = await api.get('/produto'); // Chama GET /api/produto
      setProdutos(response.data);
    } catch (error) {
      console.error('Erro ao carregar produtos:', error);
      alert('Erro ao carregar produtos');
    } finally {
      setLoading(false);
    }
  };

  // Função para adicionar produto
  const adicionarProduto = async () => {
    if (!novoProduto.trim()) return;

    try {
      const produto = { nome: novoProduto };
      const response = await api.post('/produto', produto); // Chama POST /api/produto

      // Adiciona o novo produto à lista
      setProdutos([...produtos, response.data]);
      setNovoProduto('');
      alert('Produto adicionado com sucesso!');
    } catch (error) {
      console.error('Erro ao adicionar produto:', error);
      alert('Erro ao adicionar produto');
    }
  };

  // Função para deletar produto
  const deletarProduto = async (id) => {
    try {
      await api.delete(`/produto/${id}`); // Chama DELETE /api/produto/{id}
      setProdutos(produtos.filter(p => p.id !== id));
      alert('Produto deletado com sucesso!');
    } catch (error) {
      console.error('Erro ao deletar produto:', error);
    }
  };

  return (
    <div>
      <h2>Lista de Produtos</h2>

      {/* Formulário para adicionar */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={novoProduto}
          onChange={(e) => setNovoProduto(e.target.value)}
          placeholder="Nome do produto"
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <button onClick={adicionarProduto} style={{ padding: '8px 16px' }}>
          Adicionar
        </button>
        <button onClick={carregarProdutos} style={{ marginLeft: '10px', padding: '8px 16px' }}>
          Atualizar
        </button>
      </div>

      {/* Lista de produtos */}
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <ul>
          {produtos.map((produto, index) => (
            <li key={produto.id || index} style={{ marginBottom: '10px' }}>
              {produto.nome || produto}
              <button 
                onClick={() => deletarProduto(produto.id || index)}
                style={{ marginLeft: '10px', padding: '5px 10px' }}
              >
                Excluir
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ProdutoList;
